import React, { useRef } from 'react'
import { useState } from 'react';
import { useEffect } from 'react'
import $ from "jquery"
import 'datatables.net';
import 'datatables.net-dt/css/jquery.dataTables.css';
import ConnectContract from '../Connection/ConnectContract2';
import ConnectMetamask from '../Connection/ConnectMetaMask';
import axios from "axios";
import Context from "../../index";
import { useContext } from 'react';
export default function ManufReceivedReq() {
  const [transaction,setTransaction]=useState({});
  const data=useContext(Context);
  const tableRef = useRef(null);
    const [res,setRes]=useState([]);

    ConnectMetamask();
    ConnectContract();
    useEffect(()=>{
      const postTransaction = async () => {
        try {
          const response = await axios.post("http://localhost:4000/postTransaction", {
            ...transaction
          }, {
            withCredentials: true
          });
          console.log(response.data); // Log the response from the server
        } catch (error) {
          console.log(error);
        }
      };
    
      if (Object.keys(transaction).length > 0) {
        postTransaction();
      }
      

    },[transaction]);
    useEffect(() => {
      if (res.length > 0 ) {
        $(tableRef.current).DataTable();
      }
      
    }, [res]);
    useEffect(()=>{
        const fetchdata=async()=>{
          
            const res=await window.contract1.methods.getallmanuf().call();
            console.log(res);
            setRes(res);

          
            
        }
        fetchdata();
      
    },[]);
    const accept=async(row)=>{
       try{
        await window.contract1.methods.ManufAccept(parseInt(row.reqno),row.from).send({from:data[localStorage.getItem("ROLENAME")]})
        .then(async(data)=>{ 
           
        const currentTime = new Date().toLocaleString('en-IN', { timeZone: 'Asia/Kolkata' });
        const details={user:localStorage.getItem("ROLENAME"),TransactionHash:data.transactionHash,from:data.from,to:data.to,time:currentTime,gasUsed:Number(data.gasUsed)};
        setTransaction(details);
        console.log(transaction);
        window.location.reload();
      });
       }
       catch(err){
        console.log(err);
       }

    }
  return (
    <>
    <table className="table table-striped table-reqreceived" ref={tableRef}>
        <thead>
          <tr>
            <th scope="col">RequestNo</th>
            <th scope="col">Reqorigin</th>
            <th scope="col">Reqfrom</th>
            <th scope="col">Products</th>
            <th scope="col">Quantities</th>
            <th scope="col">Date</th>
            
            <th scope="col">Accept</th>
            
          </tr>
        </thead>
        <tbody>
        {
            res.map((row,i)=>{
                return (
                    row.status==="pending" && 
                    <tr key={i}>
                        <td>{parseInt(row.reqno)}</td>
                        <td>{row.from}</td>
                        <td>{row.reqfrom}</td>
                        <td>{row.products}</td>
                        <td>{row.quantities.join(",")}</td>
                        
                        <td>{row.date}</td>
                        <td>
                            <button className='btn btn-primary' onClick={()=>accept(row)}>Accept</button>
                        </td>
                    </tr>
                )
            })
            
        }
  
      </tbody>

      </table>

       
    </>
  )
}
