import axios from "axios";
import { useEffect, useState } from "react";
import { useCookies } from "react-cookie";
import { BrowserRouter as Router, useNavigate } from "react-router-dom";
import { toast } from "react-toastify";

export default function Navbar() {
  const navigate=useNavigate();
  const [adstname, setAdstname] = useState('');
  const [cookies,setCookie,removeCookie]=useCookies([]);
  useEffect(()=>{
    const verifyUser=async ()=>{
      if(!cookies.jwt){
        navigate('/login');
      }
      else{
        const {data}=await axios.post("http://localhost:4000",{},{withCredentials:true});
        if(!data.status){
          removeCookie("jwt");
          navigate("/login");
        }
        else{
          toast(` HI {data.user}`,{theme:"dark"}); 
        }
      }
    }
  })

  useEffect(() => {
    const storedRoleName = localStorage.getItem('ROLENAME');
    console.log(localStorage.getItem('ROLENAME'));
    if (storedRoleName) {
      setAdstname(storedRoleName);
    }
  }, []);
  const  logout=()=>{
    removeCookie("jwt");
    localStorage.removeItem("ROLENAME"); // Remove adstname from localStorage
    setAdstname('NA'); // Reset adstname to empty string
    
    navigate('/login');
  }
  return (
   <header>
   
    <nav className="navbar">
    <ul className="navbar-nav w-100 justify-content-between">
        <li className="nav-item">
          <a href="selectionpage.html">
            <button className="btn btn-dark">Home</button>
          </a>
        </li>
        <li className="nav-item" id="Dashboard">
          <h2>{adstname}</h2>
        </li>
        <li className="nav-item" id="Logout">
          <button className="btn btn-dark" onClick={logout}  >Logout</button>
        </li>
      </ul>
    </nav>
   </header>
  );
}
