import React, { useState } from 'react'
import { Link,useNavigate } from 'react-router-dom';
import { toast, ToastContainer } from 'react-toastify';
import axios from "axios";
export default function Login(){
    const navigate=useNavigate();
    const [values,setValues]=useState({
        email:"",
        password:"",

    });


    const generateError=(err)=>toast.error(err,{
        position:"bottom-right"
    })

    const handleSubmit=async(e)=>{
        e.preventDefault();
        try{
            const {data}=await axios.post("http://localhost:4000/login",
            {
                ...values,
            },{
                withCredentials:true
            }
            );
           
            if(data){
                if(data.errors){
                    const {email,password}=data.errors;
                    if(email){
                        generateError(email);

                    }
                    else if(password){
                        generateError(password);

                    }
                }
                else{
                    console.log(values.password);
                    if(values.password.length===7 && (values.password==="D1ADST1" || values.password==="D1ADST2" || values.password==="D2ADST1" || values.password==="D2ADST2" ) ){
                        localStorage.setItem("ROLENAME",values.password);
                        navigate(`/${values.password}/Dashboard`);
                    }
                    else if(values.password.length===9 && (values.password==="DIVISION1" || values.password==="DIVISION2")){
                        localStorage.setItem("ROLENAME",values.password);
                        navigate(`/${values.password}/Dashboard`);
                    }
                    else if(values.password==="DGST"){
                        localStorage.setItem("ROLENAME",values.password);
                        navigate(`/${values.password}/Dashboard`);
                    }
                    else if(values.password==="MANUFACTURER"){
                        localStorage.setItem("ROLENAME",values.password);
                        navigate(`/${values.password}/Dashboard`);

                    }
                }
            }
        }
        catch(err){
            console.log(err);
        }
    }
  return(
    <div>
      <div className='container'>
        <h2>Login Account</h2>
        {/* <form onSubmit={(e)=>handleSubmit(e)}>
            <div>
                <label htmlFor='email'>Email</label>
                <input type='email' name='email' placeholder='Email' onChange={(e)=>setValues({...values,[e.target.name]:e.target.value})}/>
            </div>
            <div>
                <label htmlFor='password'>Password</label>
                <input type='password' name='password' placeholder='Password' onChange={(e)=>setValues({...values,[e.target.name]:e.target.value})}/>
            </div>
            <button type="submit">Submit</button>
            <span>
                Already have an account? <Link to='/register'>Register</Link>
            </span>
            <ToastContainer/>
        </form> */}
        <form>
  <div class="mb-3">
    <label for="exampleInputEmail1" class="form-label">Email address</label>
    <input type="email" placeholder='email' class="form-control" onChange={(e)=>setValues({...values,[e.target.name]:e.target.value})} aria-describedby="emailHelp"/>
    <div id="emailHelp" class="form-text">We'll never share your email with anyone else.</div>
  </div>
  <div class="mb-3">
    <label for="exampleInputPassword1" class="form-label">Password</label>
    <input type="password" class="form-control" id="exampleInputPassword1"/>
  </div>
  <div class="mb-3 form-check">
    <input type="checkbox" class="form-check-input" id="exampleCheck1"/>
    <label class="form-check-label" for="exampleCheck1">Check me out</label>
  </div>
  <button type="submit" class="btn btn-primary">Submit</button>
</form>
    </div>
    </div>
  )
}
