import React, { useContext, useState, useCallback } from "react";
import "./addtodiv.css";
import Context from "../../index";

export default function AddDivisions() {
  const data = useContext(Context);
  const account = data[localStorage.getItem("ROLENAME")];

  const [divisionsCount, setDivisionsCount] = useState(0);
  const [divisions, setDivisions] = useState([]);

  const handleConfirm = useCallback((e) => {
    e.preventDefault();
    const newDivisions = Array.from({ length: divisionsCount }, () => ({
      divisionName: "",
      password: "",
      noOfAdsts: 0,
      adsts: [],
    }));
    setDivisions(newDivisions);
  }, [divisionsCount]);

  const handleInputChange = useCallback((index, field, value) => {
    const updatedDivisions = [...divisions];
    updatedDivisions[index][field] = value;
    setDivisions(updatedDivisions);
  }, [divisions]);

  const handleAdstSubmit = useCallback((index) => {
    const updatedDivisions = [...divisions];
    const division = updatedDivisions[index];
    const noOfAdsts = division.noOfAdsts;
    division.adsts = Array.from({ length: noOfAdsts }, () => ({
      role: "",
      password: "",
    }));
    setDivisions(updatedDivisions);
  }, [divisions]);

  const handleAdstInputChange = useCallback((divisionIndex, adstIndex, field, value) => {
    const updatedDivisions = [...divisions];
    updatedDivisions[divisionIndex].adsts[adstIndex][field] = value;
    setDivisions(updatedDivisions);
  }, [divisions]);

 
    const handleFinalSubmit = useCallback(async (e) => {
      e.preventDefault();
    
      let divisionsf = divisions.map((division) => division.divisionName);
      let adstsf = divisions
        .filter((division) => division.adsts.length > 0)
        .map((division) => division.adsts.map((adst) => adst.role));
    
      // Create a new array by spreading the divisionsf array
      const arr = [...divisionsf];
      
    
      await window.contract.methods.addunits(arr, adstsf).send({ from: account });
    }, [divisions]);
    

  return (
    <>
      <div className="container addtodiv my-5">
        <form className="container formaddtodiv col-4" onSubmit={handleConfirm}>
          <label htmlFor="NumberofDivisions" className="form-label">
            Number of Divisions
          </label>
          <div className="mb-3 d-flex">
            <input
              type="number"
              className="form-control mx-3"
              id="NumberofDivisions"
              value={divisionsCount}
              onChange={(e) => setDivisionsCount(e.target.value)}
            />
            <button type="submit" className="btn btn-primary">
              Confirm
            </button>
          </div>
        </form>
        {divisions.map((division, divisionIndex) => (
          <div key={divisionIndex} className="row mb-3">
            <div className="col-5 d-flex fielddiv my-3">
              <input
                type="text"
                className="form-control mx-2"
                value={division.divisionName}
                onChange={(e) =>
                  handleInputChange(divisionIndex, "divisionName", e.target.value)
                }
                placeholder="Division Name"
              />
              <input
                type="text"
                className="form-control mx-2"
                value={division.password}
                onChange={(e) =>
                  handleInputChange(divisionIndex, "password", e.target.value)
                }
                placeholder="Password"
              />
              <input
                type="number"
                className="form-control mx-2"
                value={division.noOfAdsts}
                onChange={(e) =>
                  handleInputChange(divisionIndex, "noOfAdsts", e.target.value)
                }
                placeholder="Number of Adsts"
              />
              <button
                className="btn btn-primary"
                onClick={() => handleAdstSubmit(divisionIndex)}
              >
                Confirm
              </button>
            </div>
            {division.adsts.map((adst, adstIndex) => (
              <div key={adstIndex} className="row mb-2">
                <div className="col-5 offset-2">
                  <input
                    type="text"
                    className="form-control"
                    value={adst.role}
                    onChange={(e) =>
                      handleAdstInputChange(divisionIndex, adstIndex, "role", e.target.value)
                    }
                    placeholder="Role"
                  />
                </div>
                <div className="col-5">
                  <input
                    type="password"
                    className="form-control"
                    value={adst.password}
                    onChange={(e) =>
                      handleAdstInputChange(divisionIndex, adstIndex, "password", e.target.value)
                    }
                    placeholder="Password"
                  />
                </div>
              </div>
            ))}
          </div>
        ))}
        {divisions.length > 0 && (
          <div className="text-center">
            <button type="submit" className="btn btn-primary" onClick={handleFinalSubmit}>
              Confirm All
            </button>
          </div>
        )}
      </div>
    </>
  );
}
