import { useState } from "react";
import "./addtodiv.css";

export default function AddDivisions() {
  window.document.body.style.backgroundColor = "white";

  const [divisions, setDivisions] = useState([]);

  const handleSubmit = (e) => {
    e.preventDefault();
    // Generate three input fields in a row
    const newRow = { inputs: ["", "", ""] };
    setDivisions([...divisions, newRow]);
  };

  const handleInputChange = (rowIndex, inputIndex, value) => {
    const updatedDivisions = [...divisions];
    updatedDivisions[rowIndex].inputs[inputIndex] = value;
    setDivisions(updatedDivisions);
  };

  return (
    <>
      <div className="container addtodiv my-5">
        <form className="container formaddtodiv col-4" onSubmit={handleSubmit}>
          <label htmlFor="NumberofDivisions" className="form-label">
            Number of Divisions
          </label>
          <div className="mb-3 d-flex">
            <input
              type="number"
              className="form-control mx-3"
              id="NumberofDivisions"
              onChange={(e) => setDivisionscount(e.target.value)}
            />
            <button type="submit" className="btn btn-primary">
              Confirm
            </button>
          </div>
        </form>
        {divisions.map((row, rowIndex) => (
          <div key={rowIndex} className="row mb-3">
            {row.inputs.map((input, inputIndex) => (
              <div key={inputIndex} className="col">
                <input
                  type="text"
                  className="form-control"
                  value={input}
                  onChange={(e) =>
                    handleInputChange(rowIndex, inputIndex, e.target.value)
                  }
                />
              </div>
            ))}
          </div>
        ))}
      </div>
    </>
  );
}
