import React, { useEffect, useState } from "react";
import $ from 'jquery';
import 'datatables.net';
import axios from "axios";
import "./transactions.css"
function GetTransactions() {
    const [data, setData] = useState([]);
    window.document.body.style.backgroundColor = " rgb(213, 233, 219)"

    const [modaldata,setModaldata]=useState({});

    useEffect(() => {
        const fetchData = async () => {
            try {
                const response = await axios.get("http://localhost:4000/GetTransactions", {
                    params: { ROLENAME: localStorage.getItem("ROLENAME") }
                });
                const { data } = response;
                if (data.found === false) {
                    window.alert("Login to get details");
                } else {
                    const { TransactionHash, from, to, time, gasUsed } = data.data;
                    const transactions = [];
                    for (let i = 0; i < TransactionHash.length; i++) {
                        transactions.push({
                            TransactionHash: TransactionHash[i],
                            from: from[i],
                            to: to[i],
                            time: time[i],
                            gasUsed: gasUsed[i]
                        });
                    }
                    setData(transactions);
                }
            } catch (error) {
                console.error("Error fetching data:", error);
            }
        };

        fetchData();
    }, []);

    useEffect(() => {
        if (data.length > 0) {
            $('#myTable').DataTable();
        }
    }, [data]);
    const AddModaldata=()=>{

    }

    console.log(data);

    return (
        <>
            <div className="container trans-cont">
                {
                    data.map((transactions, i) => {
                        return (
                            <div class="card my-4 trans-card-box">
                                <div class="card-header d-flex justify-content-between">
                                    <h5> From Address: {transactions.from}</h5>
                                    <button className="btn btn-success" onClick={()=>add}>Get Details</button>
                                </div>
                                <div class="card-body d-flex justify-content-between">
                                    <p>Hash :{transactions.TransactionHash}</p>
                                    <p >Time is: {transactions.time}</p>


                                </div>
                               
                            </div>
                        )
                    })
                }

            </div>
        </>
    );
}

export default GetTransactions;
