import { useState } from "react";
import "./addtodiv.css";

export default function AddDivisions() {
  window.document.body.style.backgroundColor = "white";

  const [divisionsCount, setDivisionsCount] = useState(0);
  const [divisions, setDivisions] = useState([]);
  const [finalData, setFinalData] = useState(null);

  const handleConfirm = (e) => {
    e.preventDefault();
    // Generate input field rows based on divisionsCount
    const newDivisions = Array.from({ length: divisionsCount }, () => ({
      divisionName: "",
      noOfAdsts: "",
      adsts: [],
    }));
    setDivisions(newDivisions);
  };

  const handleInputChange = (index, field, value) => {
    const updatedDivisions = [...divisions];
    updatedDivisions[index][field] = value;
    setDivisions(updatedDivisions);
  };

  const handleAdstInputChange = (divisionIndex, adstIndex, value) => {
    const updatedDivisions = [...divisions];
    updatedDivisions[divisionIndex].adsts[adstIndex] = value;
    setDivisions(updatedDivisions);
  };

  const handleFinalSubmit = (e) => {
    e.preventDefault();
    setFinalData(divisions);
    // Perform any action with the finalData object
  };

  return (
    <>
      <div className="container addtodiv my-5">
        <form className="container formaddtodiv col-4" onSubmit={handleConfirm}>
          <label htmlFor="NumberofDivisions" className="form-label">
            Number of Divisions
          </label>
          <div className="mb-3 d-flex">
            <input
              type="number"
              className="form-control mx-3"
              id="NumberofDivisions"
              value={divisionsCount}
              onChange={(e) => setDivisionsCount(parseInt(e.target.value))}
            />
            <button type="submit" className="btn btn-primary">
              Confirm
            </button>
          </div>
        </form>
        <form onSubmit={handleFinalSubmit}>
          {divisions.map((division, divisionIndex) => (
            <div key={divisionIndex} className="row mb-3 justify-content-center">
              <div className="col-5 d-flex">
                <input
                  type="text"
                  className="form-control mx-2"
                  value={division.divisionName}
                  onChange={(e) =>
                    handleInputChange(divisionIndex, "divisionName", e.target.value)
                  }
                  placeholder="Division Name"
                />
                <input
                  type="number"
                  className="form-control mx-2"
                  value={division.noOfAdsts}
                  onChange={(e) =>
                    handleInputChange(divisionIndex, "noOfAdsts", e.target.value)
                  }
                  placeholder="Number of Adsts"
                />
              </div>
              <div className="col-5 d-flex">
                {Array.from({ length: division.noOfAdsts }, (_, adstIndex) => (
                  <input
                    key={adstIndex}
                    type="text"
                    className="form-control mx-2"
                    value={division.adsts[adstIndex] || ""}
                    onChange={(e) =>
                      handleAdstInputChange(divisionIndex, adstIndex, e.target.value)
                    }
                    placeholder={`Adst ${adstIndex + 1}`}
                  />
                ))}
              </div>
            </div>
          ))}
          {divisions.length > 0 && (
            <div className="text-center">
              <button type="submit" className="btn btn-primary">
                Confirm All
              </button>
            </div>
          )}
        </form>
        {finalData && (
          <div className="mt-3">
            <h3>Final Data:</h3>
            <pre>{JSON.stringify(finalData, null, 2)}</pre>
          </div>
        )}
      </div>
    </>
  );
}
