import React, { useEffect, useState } from 'react';
import './trackorder.css';
import axios from 'axios';
import { FaCheck, FaTimes } from 'react-icons/fa';

export default function Trackorder() {
  window.document.body.style.backgroundColor = "rgb(244, 245, 234)";
  const [trackOrder, setTrackOrder] = useState();
  const [selectedRequestId, setSelectedRequestId] = useState(null);

  useEffect(() => {
    const fetchData = async () => {
      try {
        const { data } = await axios.get('http://localhost:4000/getTrackOrders', { params: { user: localStorage.getItem('ROLENAME') } });
        console.log(data);
        setTrackOrder(data.trackorder);
      }
      catch (err) {
        console.log(err);
      }
    };
    fetchData();
  }, []);

  const handleTrackOrderClick = (requestId) => {
    setSelectedRequestId(requestId);
  };
  console.log(trackOrder);

  return (
    <>
      {trackOrder &&
        <div className='container my-4'>
          <div className="card-container track-cont">
            {trackOrder.requests &&
              trackOrder.requests.map((request, index) => (
                <div className="card card-contTrack my-3" key={index}>
                  <div className="card-body">
                    <div className="card-item" key={request.requestno}>
                      <h3 className="card-subtitle mb-2">Request Number: {request.requestno}</h3>
                      <p className="card-text">ID: {trackOrder._id}</p>
                      <button
                        className="btn btn-primary"
                        data-bs-toggle="modal"
                        data-bs-target="#trackModal"
                        onClick={() => handleTrackOrderClick(request._id)}
                      >
                        Get Track Order
                      </button>
                    </div>
                  </div>
                </div>
              ))}
            <div className="  modal " id="trackModal">
              <div className="modal-dialog">
                <div className="modal-content">
                  <div className="modal-header">
                    <h5 className="modal-title">Request Details</h5>
                    <button type="button" className="btn-close" data-bs-dismiss="modal"></button>
                  </div>
                  <div className="modal-body">
                    {trackOrder.requests &&
                      trackOrder.requests.map((request, i) =>
                        request._id === selectedRequestId && (
                          <React.Fragment key={request._id}>
                            <div>
                            
                              <ul className="stepper" id="stepper-form-example" data-mdb-stepper-linear="true">
                                <form className="needs-validation stepper-form">
                                  
                                  <li className="stepper-step stepper-active">
                                    <div className="stepper-head">
                                      <span className="stepper-head-icon">1</span>
                                      <span className="stepper-head-text">step1</span>
                                    </div>
                                    <div className="stepper-content py-3">
                                      

                                      

                                      
                                    </div>
                                  </li>

                                  <li className="stepper-step">
                                    <div className="stepper-head">
                                      <span className="stepper-head-icon">2</span>
                                      <span className="stepper-head-text">step2</span>
                                    </div>
                                    <div className="stepper-content py-3">
                                      
                                      

                                      
                                    </div>
                                  </li>

                                  <li className="stepper-step">
                              
                                    <div className="stepper-content py-3">
                                      

                                      <button type="submit" className="btn btn-success btn-block mb-4">Place order</button>
                                    </div>
                                  </li>

                                </form>
                              </ul>

                              <div className="d-flex justify-content-center px-3">
                                <button id="form-example-prev-step" className="btn btn-primary w-100 me-2">Previous step</button>
                                <button id="form-example-next-step" className="btn btn-primary w-100">Next step</button>
                              </div>

                            </div>

                            {request.desc.map((description, index) => (
                              <ul>
                                <li>
                                  {description}
                                </li>
                              </ul>




                            ))}

                          </React.Fragment>
                        )
                      )}
                  </div>
                </div>
              </div>
            </div>
          </div>

        </div>
      }
    </>

  );
}
