import React, { useEffect } from "react";
import { useNavigate } from "react-router-dom";
import axios from "axios";
import { useCookies } from "react-cookie";
import { toast, ToastContainer } from "react-toastify";
import Registered from "../pages/Registered";
import img from "./Screenshot (313).png";

export default function Navbar() {
  const navigate = useNavigate();
  const [cookies, setCookie, removeCookie] = useCookies([]);

  useEffect(() => {
    if (localStorage.getItem("ROLENAME")) {
      document.getElementById("login").innerHTML = "Logout";
    } else {
      document.getElementById("dashboard").style.display = "none";
      document.getElementById("login").innerHTML = "Login";
    }
  }, []);

  const dashboard = async () => {
    if (localStorage.getItem("ROLENAME")) {
      navigate(`/${localStorage.getItem("ROLENAME")}/Dashboard`);
    } else {
      window.alert("Login to Continue!");
    }
  };

  const logout = async () => {
    try {
      const data = await axios.post(
        "http://localhost:4000/logout",
        {},
        { withCredentials: true }
      );
      console.log(data);
    } catch (err) {
      console.log(err);
    }
    localStorage.removeItem("ROLENAME");
    navigate("/login");
    window.location.reload();
  };
  const home=()=>{
    navigate("/");
  }

  return (
   

    <>
      <nav class="navbar navbar-expand-lg bg-dark">
        <div class="container-fluid">

          <button class="navbar-toggler" type="button" data-bs-toggle="collapse" data-bs-target="#navbarSupportedContent" aria-controls="navbarSupportedContent" aria-expanded="false" aria-label="Toggle navigation">
            <span class="navbar-toggler-icon"></span>
          </button>
          <div class="collapse navbar-collapse" id="navbarSupportedContent">
            <ul class="navbar-nav me-auto mb-2 mb-lg-0">
              <li class="nav-item">
                <button className="btn btn-primary" onClick={()=>home}>Home</button>
              </li>
              <h2 style={{color:"white",marginLeft:"500px"}}>{localStorage.getItem("ROLENAME")}</h2>

            </ul>
            <form className="nav-item d-flex" role="search">
              <button className="btn btn-primary me-3"  id="dashboard" onClick={dashboard}>Dashboard</button>
              <button className="btn btn-primary me-3" type="button" data-bs-toggle="modal" data-bs-target="#exampleModal">Process</button>
              <button class="btn btn-primary" type="submit" id="login" onClick={logout}>Logout</button>
            </form>
          </div>
        </div>
      </nav>
      <div
        className="modal fade"
        id="exampleModal"
        tabIndex="-1"
        aria-labelledby="exampleModalLabel"
        aria-hidden="true"
      >
        <div className="modal-dialog modal-xl modal-dialog-centered">
          <div className="modal-content">
            <div className="modal-body">
              <img src={img} alt="" className="img-fluid" />
            </div>
            <div className="modal-footer">
              <button
                type="button"
                className="btn btn-dark"
                data-bs-dismiss="modal"
              >
                Close
              </button>
            </div>
          </div>
        </div>
      </div>

    </>

  );
}
