                    if(values.password==="D1ADST2"){

                    }
                    else if(values.password==="D2ADST1"){

                    }
                    else if(values.password==="D2ADST2"){

                    }
                    if(values.password==="DDST"){

                    }
                    if(values.password==="DGST"){

                    }
                    if(values.password==="MANUFACTURER"){

                    }

                    navigate('/registered');
                }
            }
        }
        catch(err){
            console.log(err);
        }
    }
  return(
    <div>
      <div className='container'>
        <h2>Login Account</h2>

        <form onSubmit={(e)=>handleSubmit(e)}>
            <div>
                <label htmlFor='email'>Email</label>
                <input type='email' name='email' placeholder='Email' onChange={(e)=>setValues({...values,[e.target.name]:e.target.value})}/>
            </div>
            <div>
                <label htmlFor='password'>Password</label>
                <input type='password' name='password' placeholder='Password' onChange={(e)=>setValues({...values,[e.target.name]:e.target.value})}/>
            </div>
            <button type="submit">Submit</button>
            <span>
                Already have an account? <Link to='/register'>Register</Link>
            </span>
            <ToastContainer/>
        </form>
    </div>
    </div>
  )
}
