export default function SendReq(){
    const putData = async(adstname, product, quantity, date, action) => {
        const count=parseInt(await window.contract.methods.getrequestcount(document.getElementById("adstname").innerText).call());
       
        products.push(product);
        quantities.push(quantity);
       
        if(e=="notsubmit"){
        table.row.add({'requestno':count+1,'product':product,'quantity':quantity,'date':date}).draw();
        }
        if(e=="submit"){
            table.row.add({'requestno':count+1,'product':product,'quantity':quantity,'date':date}).draw();
            
            console.log(document.getElementById("adstname").innerText,products,quantities,date);
            let dup=document.getElementById("adstname").innerText;
            let ref="";
            for(let i=0;i<2;i++){
                ref+=dup[i];
            }
            if(ref=="D1"){
                dup="DIVISION1";
            }
            if(ref=="D2"){
                dup="DIVISION2";
            }
            if(ref=="D3"){
                dup="DIVISION3";
            }
        await window.contract.methods.fillform(dup,document.getElementById("adstname").innerText,products,quantities,date).send({from:account});
        }
        
      };
    return (
        <>
        <div className="form bs-tooltip-end">
      
      <h>Enter product</h>
      <input  type="text"  placeholder="product"  id="product" className="form-control" list="browser" name="browser"/>
      <datalist id="browser">
        <option value="Wheat"></option>
        <option value="Grains"></option>
        <option value="Eggs"></option>
        <option value="Rice"></option>
        <option value="Meals"></option>
      </datalist>
      <h> Enter Quantity</h>
      <input type="number" className="form-control" placeholder="quantity" min="1" id="quantity"/>
      <h>Enter date</h>
      <input type="date" className="form-control" placeholder="date" id="date1"/>
      <button className="btn btn-dark" onClick="putdata(document.getElementById('adstname').innerText,document.getElementById('product').value,document.getElementById('quantity').value,document.getElementById('date1').value,'notsubmit')">Addmore</button>
      <hr />
      <h3>Details of your request</h3>

      <table id="example" style={{width: "100%"}}></table>
    </div>
    
    <button className="btn btn-dark" onClick={() => putData('Submit')} id="button">
        Submit
      </button>
        </>
    );
}
