import { useState } from "react";
import "./addtodiv.css";

export default function AddDivisions() {
  window.document.body.style.backgroundColor = "white";

  const [divisionsCount, setDivisionsCount] = useState(0);
  const [divisions, setDivisions] = useState([]);

  const handleConfirm = (e) => {
    e.preventDefault();
    // Generate input field rows based on divisionsCount
    const newDivisions = Array.from({ length: divisionsCount }, () => ({
      divisionName: "",
      noOfAdsts: "",
    }));
    setDivisions(newDivisions);
  };

  const handleInputChange = (index, field, value) => {
    const updatedDivisions = [...divisions];
    updatedDivisions[index][field] = value;
    setDivisions(updatedDivisions);
  };

  return (
    <>
      <div className="container addtodiv my-5">
        <form className="container formaddtodiv col-4" onSubmit={handleConfirm}>
          <label htmlFor="NumberofDivisions" className="form-label">
            Number of Divisions
          </label>
          <div className="mb-3 d-flex">
            <input
              type="number"
              className="form-control mx-3"
              id="NumberofDivisions"
              value={divisionsCount}
              onChange={(e) => setDivisionsCount(parseInt(e.target.value))}
            />
            <button type="submit" className="btn btn-primary">
              Confirm
            </button>
          </div>
        </form>
        {divisions.map((division, index) => (
          <div key={index} className="row mb-3">
            <div className="col-5 d-flex">
              <input
                type="text"
                className="form-control mx-2"
                value={division.divisionName}
                onChange={(e) =>
                  handleInputChange(index, "divisionName", e.target.value)
                }
                placeholder="Division Name"
              />
              <input
                type="number"
                className="form-control mx-2"
                value={division.noOfAdsts}
                onChange={(e) =>
                  handleInputChange(index, "noOfAdsts", parseInt(e.target.value))
                }
                placeholder="Number of Adsts"
              />
            </div>
            <div className="col-2">
              <button className="btn btn-primary">Confirm</button>
            </div>
          </div>
        ))}
      </div>
    </>
  );
}
