import React, { useEffect, useState } from "react";
import $ from 'jquery';
import 'datatables.net';
import axios from "axios";

function GetTransactions() {
    const [data, setData] = useState([]);

    useEffect(() => {
        const fetchData = async () => {
            try {
                const response = await axios.get("http://localhost:4000/GetTransactions", {
                    params: { ROLENAME: localStorage.getItem("ROLENAME") }
                });
                const { data } = response;
                if (data.found === false) {
                    window.alert("Login to get details");
                } else {
                    const { TransactionHash, from, to, time, gasUsed } = data.data;
                    const transactions = [];
                    for (let i = 0; i < TransactionHash.length; i++) {
                        transactions.push({
                            TransactionHash: TransactionHash[i],
                            from: from[i],
                            to: to[i],
                            time: time[i],
                            gasUsed: gasUsed[i]
                        });
                    }
                    setData(transactions);
                }
            } catch (error) {
                console.error("Error fetching data:", error);
            }
        };

        fetchData();
    }, []);

    useEffect(() => {
        if (data.length > 0) {
            $('#myTable').DataTable();
        }
    }, [data]);

    console.log(data);

    return (
        <>
            <div className="container col-12 table-responsive p-5">
                <table className="table table-striped" id="myTable">
                    <thead>
                        <tr>
                            <th scope="col">TransactionHash</th>
                            <th scope="col">From</th>
                            <th scope="col">To</th>
                            <th scope="col">Time</th>
                            <th scope="col">GasUsed</th>
                        </tr>
                    </thead>
                    <tbody>
                        {data.map((transaction, index) => (
                            <tr key={index}>
                                <td>{transaction.TransactionHash}</td>
                                <td>{transaction.from}</td>
                                <td>{transaction.to}</td>
                                <td>{transaction.time}</td>
                                <td>{transaction.gasUsed}</td>
                            </tr>
                        ))}
                    </tbody>
                </table>
            </div>
        </>
    );
}

export default GetTransactions;
