import { useState } from "react";
import { useEffect } from "react";
import ConnectContractSR from "./ConnectContractSR";
import ConnectMetamask from "./ConnectMetamask";

export default function ReceivedReq(){
    const [res,setRes]=useState([]);
    const [ref,setRef]=useState([]);
    const [ref2,setRef2]=useState([]);

    const accept=async()=>{

    }
    const decline=async()=>{

    }
    const reftask=async(i)=>{
        
        setRef(await window.contract.methods.getallreqreceivedddst(i).call());
    }
    const ref2task=async(i)=>{
        
        setRef2(await window.contract1.methods.getallreqreceivedddst(i).call());
        for(let k=0;k<ref2.length;k++){	
            ref.push(ref2[k]);
        }
        
    }
    
    ConnectMetamask();
    ConnectContractSR();
    useEffect(()=>{
        const fetchdata=async()=>{
            let res=await window.contract.methods.getAllreqreceive(localStorage.getItem("ROLENAME")).call();
		    let res2=await window.contract1.methods.getalladsts(localStorage.getItem("ROLENAME")).call();
            let updated=[...res];
            for(let i=0;i<res2.length;i++){
                for(let j=0;j<updated.length;j++){
                    if(updated[j].reqno===res2[i].reqno && updated[j].from===res2[i].from){
                        updated[j]=res2[i];
                    }
                }
            }
            if(updated.length===0){
               updated=[...res2];
            }
            setRes(updated);
            

        }
        fetchdata();
        
        
    },[]);
 console.log(ref,ref2);
    return (
       
    
   <>
   <div className="container">
   <table id="myTable" className="table table-striped table-hover">
             <thead>
                <tr>
                    <td>Request No</td>
                    <td>Request From</td>
                    <td>From</td>
                    <td>Products</td>
                    <td>Quantities</td>
                    <td>Date</td>
                    <td>Accept</td>
                    <td>Reject</td>
                </tr>
             </thead>
             <tbody>
             {res.map((ele, i) => {
                    reftask(ele.reqfrom);
                    ref2task(ele.reqfrom);
                    return (
                        <tr key={i}>
                        <td>{parseInt(ele.reqno)}</td>
                        <td>{ele.reqfrom}</td>
                        <td>{ele.from}</td>
                        <td>{ele.products}</td>
                        <td></td>
                        <td></td>
                    </tr>
                    );
            })}

             </tbody>
    </table>
   </div>
   </>
    );
}
