import React, { useState } from 'react'
import { Link, useNavigate } from 'react-router-dom';
import { toast, ToastContainer } from 'react-toastify';
import axios from "axios";
import { useEffect } from 'react';
import CoverImage from "./wp2579124.jpg"
export default function Login() {

    // window.document.body.style.backgroundImage = `url(${backgroundImage})`;
    const navigate = useNavigate();
    const [values, setValues] = useState({
        email: "",
        password: "",

    });
    const generateError = (err) => toast.error(err, {
        position: "bottom-right"
    })
    const handleSubmit = async (e) => {
        e.preventDefault();
        try {
            const { data } = await axios.post("http://localhost:4000/login",
                {
                    ...values,
                }, {
                withCredentials: true
            }
            );
            if (data) {
                if (data.errors) {
                    const { email, password } = data.errors;
                    if (email) {
                        generateError(email);
                    }
                    else if (password) {
                        generateError(password);

                    }
                }
                else {
                    localStorage.setItem("ROLENAME", values.password);
                    navigate(`/${values.password}/Dashboard`);
                    window.location.reload();
                }

            }
        }
        catch (err) {
            console.log(err);
        }
    }
    return (
        <div className='container '>

            <div className="row border rounded-5 p-3 bg-white shadow box-area">
                <div className='col-md-6   '>
                    <div className='featured-image '>
                        <img src={CoverImage} className="img-fluid rounded-5" alt="" Style={{ width: '250px' }} />
                        <p>Be Verified</p>
                    </div>



                </div>
                <div className='col-md-6'>
                    <div className='row align-items-center'>
                        <div className="header-text mb-4">
                            <h4>Login Account</h4>
                        </div>
                        <div>
                            
                        </div>

                    </div>
                </div>
            </div>

        </div>


    );
}
