import React, { useRef, useState } from 'react'
import { useEffect } from 'react'
import $ from 'jquery';
import 'datatables.net';
import ConnectMetamask from '../Connection/ConnectMetaMask';
import ConnectContract from '../Connection/ConnectSmartContract';
import axios from "axios";
import Context from "../../index.js";
import { useContext } from 'react';
import "./receivedreq.css"
export default function ReceivedRequests() {
  window.document.body.style.backgroundColor="rgb(253, 253, 249)";
  const [transaction, setTransaction] = useState({});
  const tableRef = useRef();
  const [res, setRes] = useState([]);
  const data = useContext(Context);
  ConnectMetamask();
  ConnectContract();

  useEffect(() => {
    const postTransaction = async () => {
      try {
        const response = await axios.post("http://localhost:4000/postTransaction", {
          ...transaction
        }, {
          withCredentials: true
        });
        console.log(response.data); // Log the response from the server
      } catch (error) {
        console.log(error);
      }
    };

    if (Object.keys(transaction).length > 0) {
      postTransaction();
    }

  }, [transaction]);


  useEffect(() => {
    if (res.length > 0) {
      $(tableRef.current).DataTable();
    }


  }, [res]);


  useEffect(() => {

    const fetchdata = async () => {
      
      
        const res = await window.contract.methods.getAllreceivedDDST(localStorage.getItem("ROLENAME")).call();
      
      setRes(res);
      console.log(res);
    }
    fetchdata();
  }, []);
  console.log(localStorage.getItem("ROLENAME"));
  const accept = async (row) => {
    console.log(row);
    
    try {
      const hash = await window.contract.methods.adstsacceptfromdivision(parseInt(row.reqno), localStorage.getItem("ROLENAME"), row.reqfrom,row.from).send({ from: data[localStorage.getItem('ROLENAME')] })
        .then(async (data) => {
          const currentTime = new Date().toLocaleString('en-IN', { timeZone: 'Asia/Kolkata' });
          const details = { user: localStorage.getItem("ROLENAME"), TransactionHash: data.transactionHash, from: data.from, to: data.to, time: currentTime, gasUsed: Number(data.gasUsed) };

          
          const post=await axios.post("http://localhost:4000/postTrackOrder",{adstname:row.from,requestno:parseInt(row.reqno),status:"accepted",desc:`accepted by ${localStorage.getItem("ROLENAME")}`},{withCredentials:true});
          console.log(post);
          setTransaction(details);
          console.log(transaction);
          window.location.reload();
        });
    }
    catch (err) {
      console.log(err);
    }

  }
  const decline = async (row) => {
    try {
      const hash = await window.contract.methods.adstreject(parseInt(row.reqno), localStorage.getItem("ROLENAME"), row.reqfrom,row.from).send({ from: data[localStorage.getItem("ROLENAME")] })
        .then(async (data) => {
          const currentTime = new Date().toLocaleString('en-IN', { timeZone: 'Asia/Kolkata' });
          const details = { user: localStorage.getItem("ROLENAME"), TransactionHash: data.transactionHash, from: data.from, to: data.to, time: currentTime, gasUsed: Number(data.gasUsed) };
          const post=await axios.post("http://localhost:4000/postTrackOrder",{adstname:row.from,requestno:parseInt(row.reqno),status:"declined",desc:`declined by ${localStorage.getItem("ROLENAME")}`},{withCredentials:true});
          console.log(post);
          setTransaction(details);
          console.log(transaction);
          window.location.reload();
        });
    }
    catch (err) {
      console.log(err);
    }
  }
  console.log(res);
  return (
    <>
      <div className="container table-reqreceived">
        <table className="table table-striped" ref={tableRef}>
          <thead>
            <tr>
              <th scope="col">RequestNo</th>
              <th scope="col">Reqorigin</th>
              <th scope="col">Reqfrom</th>
              <th scope="col">Products</th>
              <th scope="col">Quantities</th>
              <th scope="col">Date</th>
              <th scope="col">Accept</th>
              <th scope="col">Decline</th>
            </tr>
          </thead>
          <tbody>
            {
              res.map((row, i) => {
                return (
                  row.status === "pending" &&
                  <tr key={i}>
                    <td>{parseInt(row.reqno)}</td>
                    <td>{row.from}</td>
                    <td>{row.reqfrom}</td>
                    <td>{row.products.join(",")}</td>
                    <td>{row.quantities.join(",")}</td>
                    <td>{row.date}</td>
                    <td>
                      <button className='btn btn-primary' onClick={() => accept(row)}>Accept</button>
                    </td>
                    <td>
                      <button className='btn btn-primary' onClick={() => decline(row)}>Reject</button>
                    </td>
                  </tr>
                );
              })
            }
          </tbody>

        </table>
      </div>
    </>
  )
}
