import { useState } from "react";
import { useEffect } from "react";
import ConnectContractSR from "./ConnectContractSR";
import ConnectMetamask from "./ConnectMetamask";
let account="0x85e19d642d24fe428Eb9205F077a856CA8bC95B0";
let found=true;
let found1=true;
export default function ReceivedReq(){
    const [res,setRes]=useState([]);
    const [ref,setRef]=useState([]);
   

    const accept1=async(row)=>{
        await window.contract.methods.acceptreqDDST(row.reqfrom,row.from,row.reqno,localStorage.getItem("ROLENAME")).send({from:account});

    }
    const decline1=async(row)=>{
        await window.contract.methods.declinereqDDST(row.reqfrom,row.from,row.reqno,localStorage.getItem("ROLENAME")).send({from:account});

    }
    const accept2=async(row)=>{
        let acc=localStorage.getItem("ROLENAME");
let rej="";
if(acc==="D1ADST1"){
	rej="D1ADST2";

}
if(acc==="D1ADST2"){
	rej="D1ADST1";
	
}
if(acc==="D2ADST1"){
	rej="D2ADST2";
}
if(acc==="D2ADST2"){
	rej="D2ADST1";
	
}
if(acc==="D3ADST1"){
	rej="D3ADST2";
}
if(acc==="D3ADST2"){
	rej="D3ADST1";
	
}

await window.contract1.methods.acceptreqDDST1(acc,rej,row.from,row.reqno,row.products,row.quantities,row.date).send({from:account});

    }
    const decline2=async(row)=>{
        let rej=localStorage.getItem("ROLENAME");
let check="";
if(rej==="D1ADST1"){
	check="D1ADST2";

}
if(rej==="D1ADST2"){
	check="D1ADST1";
	
}
if(rej==="D2ADST1"){
	check="D2ADST2";
}
if(rej==="D2ADST2"){
	check="D2ADST1";
	
}
if(rej==="D3ADST1"){
	check="D3ADST2";
}
if(rej==="D3ADST2"){
	check="D3ADST1";
	
}





await window.contract1.methods.declinereqDDST1(rej,check,row.reqno,row.from).send({from:account});

    }
  
    
    ConnectMetamask();
    ConnectContractSR();
    const fetchdata=async()=>{
        let res=await window.contract.methods.getAllreqreceive(localStorage.getItem("ROLENAME")).call();
        let res2=await window.contract1.methods.getalladsts(localStorage.getItem("ROLENAME")).call();
        let updated=[...res];
        for(let i=0;i<res2.length;i++){
            for(let j=0;j<updated.length;j++){
                if(updated[j].reqno===res2[i].reqno && updated[j].from===res2[i].from){
                    updated[j]=res2[i];
                }
            }
        }
        if(updated.length===0){
           updated=[...res2];
        }
        setRes(updated);
        
        let refs=[];
    
        for(let i=0;i<updated.length;i++){
            let dup_v=await window.contract.methods.getallreqreceivedddst(updated[i].reqfrom).call();	
            let dup_v2=await window.contract1.methods.getallreqreceivedddst(updated[i].reqfrom).call();
            
           
            refs = [...refs, ...dup_v, ...dup_v2];
            
        }
        setRef(refs);

        
    }
    useEffect(()=>{
       
        fetchdata();
        
    },[]);
   console.log(ref,res);
    
   

    return (
       
    
   <>
   <div className="container">
   <table id="myTable" className="table table-striped table-hover">
             <thead>
                <tr>
                    <td>Request No</td>
                    <td>Request From</td>
                    <td>From</td>
                    <td>Products</td>
                    <td>Quantities</td>
                    <td>Date</td>
                    <td>Accept</td>
                    <td>Reject</td>
                </tr>
             </thead>
             <tbody>
             {res.map((ele, i) => {
                    
                    return (
                        ele.status==="pending" && 
                       
                        <tr key={i}>
                        <td>{parseInt(ele.reqno)}</td>
                        <td>{ele.reqfrom}</td>
                        <td>{ele.from}</td>
                        <td>{ele.products}</td>
                        <td>{ele.quantities}</td>
                        <td>{ele.date}</td>
                        <td>
                            {ref.map((refele, refi) => {
                                if (
                                refele.from === ele.from &&
                                refele.reqno === ele.reqno &&
                                refele.reqfrom.length === 7
                                ) {
                                return (
                                    <button
                                    className="btn btn-dark"
                                    id={i}
                                    onClick={() => accept1(ele)}
                                    key={refi}
                                    >
                                    Accept
                                    </button>
                                );
                                } else if (
                                refele.from === ele.from &&
                                refele.reqno === ele.reqno &&
                                refele.reqfrom.length === 4 && found===true && (
                                    <button></button>
                                )
                                ) {
                                
                                }
                                return null; // Render nothing if the conditions are not met
                            })}
                            </td>

                        <td>
                        {
                                ref.map((refele,refi)=>(
                                    refele.from===ele.from && refele.reqno===ele.reqno && refele.reqfrom.length===7 && (
                                    <button className="btn btn-dark"  onClick={()=>decline1(ele)} >Decline</button>)
                                ))
                            }
                            {
                                 ref.map((refele,refi)=>(
                                    refele.from===ele.from && refele.reqno===ele.reqno && refele.reqfrom.length===4 && (
                                    <button className="btn btn-dark" onClick={()=>decline2(ele)} >Decline</button>)
                                ))
                            }
                        </td>
                        {found=true}
                        
                        
                    </tr>
                    );
            })}

             </tbody>
    </table>
   </div>
   </>
    );
}
