import React, { useEffect, useRef, useState } from 'react';
import axios from 'axios';
import 'datatables.net';
import $ from 'jquery';
import 'datatables.net-dt/css/jquery.dataTables.css';
import ConnectMetamask from '../Connection/ConnectMetaMask';
import ConnectContract from '../Connection/ConnectSmartContract';
import Context from '../../index.js';
import { useContext } from 'react';
export default function DDSTReceivedRequests() {
  const [transaction, setTransaction] = useState({});
  const data = useContext(Context);
  const tableRef = useRef();
  const [res, setRes] = useState([]);

  
  useEffect(()=>{
    ConnectMetamask();
    ConnectContract();

  },[]);
  

  useEffect(() => {
    const postTransaction = async () => {
      try {
        const response = await axios.post(
          'http://localhost:4000/postTransaction',
          { ...transaction },
          { withCredentials: true }
        );
        console.log(response.data); // Log the response from the server
      } catch (error) {
        console.log(error);
      }
    };

    if (Object.keys(transaction).length > 0) {
      postTransaction();
    }
  }, [transaction]);

  useEffect(() => {
    if (res.length > 0) {
      $(tableRef.current).DataTable();
    }
  }, [res]);

  useEffect(() => {
    
    const fetchdata = async () => {
      try {
        const response = await window.contract.methods
          .getAllreceivedDDST(localStorage.getItem('ROLENAME'))
          .call();

        setRes(response);
        console.log(response);
      } catch (error) {
        console.log(error);
      }
    };

    fetchdata();
  }, []);

  const sendtoadsts = async (row) => {
    try {
      const hash = await window.contract.methods
        .DDSTforwardRequest(
          row.reqfrom,
          localStorage.getItem('ROLENAME'),
          parseInt(row.reqno),
          row.products,
          row.quantities,
          row.date,
          row.from
        )
        .send({ from: data[localStorage.getItem('ROLENAME')] });

      const currentTime = new Date().toLocaleString('en-IN', {
        timeZone: 'Asia/Kolkata',
      });

      const details = {
        user: localStorage.getItem('ROLENAME'),
        TransactionHash: hash.transactionHash,
        from: hash.from,
        to: hash.to,
        time: currentTime,
        gasUsed: Number(hash.gasUsed),
      };

      setTransaction(details);
      console.log(details);
      window.location.reload();
    } catch (error) {
      console.log(error);
    }
  };

  const sendtoalladsts = async (row) => {
    try {
      const hash = await window.contract.methods
        .DDSTforwardRequest(
          row.reqfrom,
          localStorage.getItem('ROLENAME'),
          parseInt(row.reqno),
          row.products,
          row.quantities,
          row.date,
          row.from
        )
        .send({ from: data[localStorage.getItem('ROLENAME')] });

      const currentTime = new Date().toLocaleString('en-IN', {
        timeZone: 'Asia/Kolkata',
      });

      const details = {
        user: localStorage.getItem('ROLENAME'),
        TransactionHash: hash.transactionHash,
        from: hash.from,
        to: hash.to,
        time: currentTime,
        gasUsed: Number(hash.gasUsed),
      };

      setTransaction(details);
      console.log(details);
      window.location.reload();
    } catch (error) {
      console.log(error);
    }
  };

  const sendtodgst = async (row) => {
    try {
      const hash = await window.contract.methods
        .DDSTsendDGST(
          localStorage.getItem('ROLENAME'),
          parseInt(row.reqno),
          row.products,
          row.quantities,
          row.date,
          row.from
        )
        .send({ from: data[localStorage.getItem('ROLENAME')] });

      const currentTime = new Date().toLocaleString('en-IN', {
        timeZone: 'Asia/Kolkata',
      });

      const details = {
        user: localStorage.getItem('ROLENAME'),
        TransactionHash: hash.transactionHash,
        from: hash.from,
        to: hash.to,
        time: currentTime,
        gasUsed: Number(hash.gasUsed),
      };

      setTransaction(details);
      console.log(details);
      window.location.reload();
    } catch (error) {
      console.log(error);
    }
  };

  return (
    <>
      <table className="table table-striped" ref={tableRef}>
        <thead>
          <tr>
            <th scope="col">RequestNo</th>
            <th scope="col">Reqorigin</th>
            <th scope="col">Reqfrom</th>
            <th scope="col">Products</th>
            <th scope="col">Quantities</th>
            <th scope="col">Date</th>
            <th scope="col">Send to adsts</th>
            <th scope="col">Send to dgst</th>
          </tr>
        </thead>
        <tbody>
        
          { 
            res.map((row, i) => {
            const isPending =
              row.status === 'pending' ||
              (row.acceptedby === 'No supplies in units' &&
                (row.status === 'approvedtoadsts' ||
                  row.status === 'approvedtosomeadsts') &&
                row.reqfrom !== 'DGST');

            const isAcceptedNoSupplies =
              row.acceptedby === 'No supplies in units';

            return (
              isPending && (
                <tr key={i}>
                  <td>{parseInt(row.reqno)}</td>
                  <td>{row.from}</td>
                  <td>{row.reqfrom}</td>
                  <td>{row.products.join(',')}</td>
                  <td>{row.quantities.join(',')}</td>
                  <td>{row.date}</td>
                  <td>
                    {isAcceptedNoSupplies ? (
                      <p>NA</p>
                    ) : row.reqfrom !== 'DGST' ? (
                      <button
                        className="btn btn-primary"
                        onClick={() => sendtoadsts(row)}
                      >
                        Send to adsts
                      </button>
                    ) : (
                      <button
                        className="btn btn-primary"
                        onClick={() => sendtoalladsts(row)}
                      >
                        Send to all adsts
                      </button>
                    )}
                  </td>
                  <td>
                    {isAcceptedNoSupplies ? (
                      <button
                        className="btn btn-primary"
                        onClick={() => sendtodgst(row)}
                      >
                        Send to DGST
                      </button>
                    ) : (
                      <p>NA</p>
                    )}
                  </td>
                </tr>
              )
            );
          })}
        </tbody>
      </table>
    </>
  );
}
