import React, { useEffect, useState } from 'react';
import './trackorder.css';
import axios from 'axios';

export default function Trackorder() {
  const [trackOrder, setTrackOrder] = useState({});
  const [selectedRequest, setSelectedRequest] = useState(null);

  useEffect(() => {
    const fetchData = async () => {
      const data = await axios.get('http://localhost:4000/getTrackOrders', {
        params: { user: localStorage.getItem('ROLENAME') },
      });
      setTrackOrder(data.data.trackorder);
    };

    fetchData();
  }, []);

  const handleTrackOrderClick = (request) => {
    setSelectedRequest(request);
  };

  return (
    <div className="card-container track-cont">
      {trackOrder.requests && trackOrder.requests.map((request, index) => (
        <div className="card" key={index}>
          <h2>ADST Name: {trackOrder.adstname}</h2>
          <div className="card-item" key={request.requestno}>
            <h3>Request Number: {request.requestno}</h3>
            <p>ID: {trackOrder._id}</p>
            <button className="track-btn" onClick={() => handleTrackOrderClick(request)}>
              Track Order
            </button>
          </div>
        </div>
      ))}
      {selectedRequest && (
        <div className="modal">
          <div className="modal-content">
            <h3>Request Number: {selectedRequest.requestno}</h3>
            <ul>
              {selectedRequest.desc.map((description, index) => (
                <li key={index}>{description}</li>
              ))}
            </ul>
          </div>
        </div>
      )}
    </div>
  );
}
