import React from 'react';
import { Link } from 'react-router-dom';
import "./dashboard.css"
import bac from "./127-1920x1080-f8963ce54dd7fef3c697c37fc10b540f.jpg";
function Dashboard() {
  const containerStyle = {
    backgroundImage: `url(${backgroundImage.default})`,
    backgroundSize: '100% 100%',
    backgroundRepeat: 'no-repeat',
    backgroundPosition: 'center',
  };
  
  window.document.body.style.backgroundColor="rgb(244, 245, 234)";
  window.document.body.style.backgroundColor="silver";
  return (
    <div className='container dashboard-cont'>
      <div className='row mt-5 dflex justify-content-center'>
        <div className='col-2 d-flex flex-column '>
          <Link to={`/${localStorage.getItem('ROLENAME')}/SendRequest`} >
            <button className='btn btn-primary my-3 '>Send a Request</button>
          </Link>
          <Link to={`/${localStorage.getItem('ROLENAME')}/StatusofSend`}>
            <button className='btn btn-success my-3'>Status of Sent</button>
          </Link>
          <Link to={`/${localStorage.getItem('ROLENAME')}/ReceivedReq`} >
            <button className='btn btn-success my-3'>Received Requests</button>
          </Link>
          <Link to={`/${localStorage.getItem('ROLENAME')}/StatusofReceived`} >
            <button className='btn btn-danger my-3'>Status of Received</button>
          </Link>
          <Link to={`/${localStorage.getItem('ROLENAME')}/TrackOrder`} >
            <button className='btn btn-dark my-3'>Track Order</button>
          </Link>
          <Link to={`/${localStorage.getItem('ROLENAME')}/GetTransactions`}>
            <button className='btn btn-dark my-3'>Get Transactions</button>
          </Link>
        </div>
      </div>
    </div>
  );
}

export default Dashboard;
